 select * from
(select /*+INDEX_DESC(good_board pk_goodboard)*/
    ROWNUM as rn, bno, title, content, writer, replyCnt, writeDate, updateDate, board_id
from good_board where rownum <= #{maxRow})
where rn > #{minRow}


